import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import WidgetContainer from "./widgets/widgetContainer";
import postData from '../data/List.json'
import { Toolbar } from "@material-ui/core";
import GridLayout from 'react-grid-layout';
/**
 * The dashboard serves as the container for widgets. It should have
 * authority over the grid of widgets and their settings. It can be closed
 * and opened, ie some sort of save file/object saves the contents of the
 * dashboard, and that save can be loaded and unloaded at will, allowing
 * different dashboards.
 */
const useStyles = makeStyles(theme => ({
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
        width: '100%'
    }
}));

const layout = [
    // { i: "a", x: 0, y: 0, w: 1, h: 1 },
    // { i: "b", x: 1, y: 0, w: 1, h: 1 },
    // { i: "c", x: 2, y: 0, w: 1, h: 1 },
    // { i: "e", x: 3, y: 0, w: 1, h: 1 }
];

const Dashboard = () => {
    // const defaultProps = {
    //     className: "layout",
    //     items: 20,
    //     rowHeight: 30,
    //     onLayoutChange: function() {},
    //     cols: 12
    // };

    const classes = useStyles();
    return (
        <main className={classes.content}>
            <Toolbar />
            <GridLayout
                className="layout"
                // layout={layout}
                cols={5}
                rowHeight={320}
                width={1200}
            >
                {postData.map((postDetail, index)=>{
			  	return (<div key={index}><WidgetContainer  name={postDetail.title}/></div>)
			  	// return (<div key={index}><div>{postDetail.title}</div></div>)
			  })}

            </GridLayout>
        </main>
    );
};

export default Dashboard;
